export const addTodo = () => {
    return {
        type: ADD_TODO
    }
}
export const toggleTodo = () => {
    return {
        type: TOGGLE_TODO
    }
}
export const deleteTodo = () => {
    return {
        type: DELETE_TODO
    }
}
export const clearCompletedTodos = () => {
    return {
        type: COMPLETED_TODOS
    }
}
