import todosList from "./todos.json";
import { ADD_TODO, TOGGLE_TODO, DELETE_TODO, CLEAR_COMPLETED_TODOS, toggleTodo } from "./actions";

const initialState = {
    todos: todosList
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TODO:
        case TOGGLE_TODO:
        case DELETE_TODO:
        case CLEAR_COMPLETED_TODOS:
        default:
            return state;
    }
}

export default reducer;
